wizardCommand:
@func addEvents
  @callOnEvent layerChanged TempTowerLayerChangedEvent
  @callOnEvent jobFinished UnsubscribeTempTowerLayerChangedEvent
  @callOnEvent jobKilled UnsubscribeTempTowerLayerChangedEvent
@endfunc

@func printResults
  @echo ##########################
  @echo First Segment Offset: {{global.zOffset}}
  @echo Segment Size: {{global.segmentSize}}
  @echo ----
  @echo Calculated Segments: {{round(global.segmentCount)}}
  @echo Calculated Temperature Steps: {{round(global.tempSteps)}}Â°C/Segment
  @echo ##########################
@endfunc

@func waitForInfo
  @if {{job.of_layer==0}}
    @setTimer 0
    @waitForTimer 500
    @call waitForInfo
  @endif
@endfunc

@func calculateTempStepsAndStartPrint
  @printModelByID {{global.printID}}
  @if {{global.error == 0}}
    @call waitForInfo
    @set global.reverseDirection {{ifelse(global.TempTowerTempEnd <= global.TempTowerTempStart, 1, 0)}}
    @set local.tempDelta {{ifelse(global.reverseDirection == 1, global.tempStart - global.tempEnd, global.tempEnd - global.tempStart)}}
    @set global.segmentCount {{round(job.z_max - global.zOffset) / global.segmentSize}}
    @set global.tempSteps {{round(local.tempDelta / round(global.segmentCount - 1))}}
    @call printResults
    @set global.nextSegment {{global.zOffset}}
    @call addEvents
  @else
    @error "Error starting print: {{global.error}}. You probably did not set the G-Code ID to a print id. Open the g-code with summary to see the id."
  @endif
@endfunc

@func showTempTowerConfigDialog
  @dialogStart "This is a wizard for generating the right temperatures for a temp tower test" "Temperature Tower Wizard"
    @dialogBlockingJob
    @dialogIcon '<svg version="1.1" viewBox="-10 0 376 1000"> <path fill="currentColor" d="M171 734h109v77h-109v-77zM171 552h109v77h-109v-77zM171 370h109v77h-109v-77zM171 188h109v77h-109v-77zM80 83v834h199v-834h-199zM0 0h356v1000h-356v-1000z"></path> </svg>'
    @dialogInputInt "Start Temperature" global.tempStart default(220) min({{perm.ext_minTemp}}) max({{perm.ext_maxTemp}}) 
    @dialogInputInt "End Temperature" global.tempEnd default(180) min({{perm.ext_minTemp}}) max({{perm.ext_maxTemp}})
    @dialogInputDouble "Segment Size in mm" global.segmentSize min(1) default(6.8)
    @dialogInputDouble "First Segment Offset in mm" global.zOffset default(0.8) min(0)
    @dialogInputCheckbox "Retract before heating" global.retractionEnabled default(1)
    @dialogInputDouble "Retraction length in mm" global.retraction default(3) min(0)
    @dialogInputCheckbox "Move hotend away when heating" global.homePosEnabled default(1)
    @dialogInputDouble "X position when heating" global.moveXPos default(0) min({{config.move_x_min}}) max({{config.move_x_max}})
    @dialogInputDouble "Y position when heating" global.moveYPos default(0) min({{config.move_y_min}}) max({{config.move_y_max}})
    @dialogInputInt "Temp Tower G-Code ID" global.printID default(0) min(0)
    @dialogButton "Cancel" 
    @dialogButton "Help" "@call showTempTowerHelpDialog"
    @dialogButton "Start print" "@call calculateTempStepsAndStartPrint" 
  @dialogShow
@endfunc

@func showTempTowerHelpDialog
  @dialogStart "<div>This Temperature Tower Wizard transforms an existing, sliced g-code into a temperature test for your printer.</div><div>It will increase or decrease the temperature every segment by calculated steps.</div><div>To use this wizard, please enter the gradient data and G-Code ID.</div><div>You can find the G-Code ID by opening a g-codes summary.</div><div>Please make sure that the segment size and your first segment offset is set precisely!</div>" "Temperature Tower Wizard help"
    @dialogIcon '<svg version="1.1" viewBox="-10 0 376 1000"> <path fill="currentColor" d="M171 734h109v77h-109v-77zM171 552h109v77h-109v-77zM171 370h109v77h-109v-77zM171 188h109v77h-109v-77zM80 83v834h199v-834h-199zM0 0h356v1000h-356v-1000z"></path> </svg>'
    @dialogBlockingJob
    @dialogButton "OK" "@call showTempTowerConfigDialog" 
  @dialogShow
@endfunc

@func TempTowerLayerChangedEvent event 
  @if {{(state.pos_z >= global.nextSegment)}}
    @if {{get("global","retractionEnabled", 1) == 1}} 
      G1 E-{{global.retraction}}
    @endif
    @if {{get("global","homePosEnabled", 1) == 1}}
      @set local.lastXPos {{state.pos_x}}
      @set local.lastYPos {{state.pos_x}}
      G1 X{{global.moveXPos}} Y{{global.moveYPos}} F{{config.speed_max_xy_per_minute}}
    @endif
    @set local.newTemp {{ifelse(global.reverseDirection == 1, ext_0.temp - global.tempSteps, ext_0.temp + global.tempSteps)}}
    @set local.newTemp {{ifelse(global.zOffset == global.nextSegment, global.tempStart, local.newTemp)}}
  	M109 S{{local.newTemp}}
    @if {{get("global","homePosEnabled",1) == 1}}
      G1 X{{local.lastXPos}} Y{{local.lastYPos}} F{{config.speed_max_xy_per_minute}}
    @endif
    @if {{get("global","retractionEnabled",1) == 1}} 
      G1 E-{{global.retraction}}
    @endif
    @set global.nextSegment {{global.nextSegment + global.segmentSize}}
  @endif
@endfunc

@func UnsubscribeTempTowerLayerChangedEvent event
  @echo "Removing Temp Tower Generator Event bindings..."
  @removeCallOnEvent layerChanged TempTowerLayerChangedEvent
  @removeCallOnEvent jobFinished UnsubscribeTempTowerLayerChangedEvent
  @removeCallOnEvent jobKilled UnsubscribeTempTowerLayerChangedEvent
  @echo OK
@endfunc

@func initTempTowerWizard
  @set global.layerInterval 10
  @set global.tempSteps 0 
  @set perm.ext_minTemp 180
  @set perm.ext_maxTemp 260
  @call showTempTowerConfigDialog
@endfunc

@call initTempTowerWizard
